"use strict";
var React = require("react");

module.exports = React.createClass({
  render: function () {
    return (
      React.DOM.div({ className: "qube-questor-detail"},
        React.DOM.div(null, this.props.name),
        React.DOM.div(null, this.rarityToStars(this.props.rarity)),
        React.DOM.div(null, "Affinity: ", this.props.affinity),
        React.DOM.div(null, "Strength: ", this.props.attributes.strengthLow),
        React.DOM.div(null, "Health: ", this.props.attributes.healthLow),
        React.DOM.div(null, "Speed: ", this.props.attributes.speedLow),
        React.DOM.div(null, "xPS: ", this.props.attributes.perSecond),
        React.DOM.div(null, "First Skill: ", this.props.attributes.skills.a),
        React.DOM.div(null, "Second Skill: ", this.props.attributes.skills.b)
      )
    );
  },
  rarityToStars: require("../util/rarityToStars")
});
