"use strict";
var React = require("react");
var Router = require("react-router-component");
var Link = Router.Link;

var capitalize = function (s) {
  return s.charAt(0).toUpperCase() + s.slice(1);
};

module.exports = React.createClass({
  displayName: "QuestorDetail",
  render: function () {
    var questor = this.props.questor;

    return (
      React.DOM.div({ className: "qube-questor-detail"},
        React.DOM.div(null, questor.name),
        React.DOM.div(null, this.rarityToStars(questor.rarity)),
        React.DOM.div(null, "Affinity: ", Link({ href: questor.affinity + "/0/all" }, capitalize(questor.affinity))),
        React.DOM.div(null, "Origin: ", Link({ href: "/all/0/" + questor.origin }, questor.origin)),
        React.DOM.div(null, "Description: ", questor.description),
        React.DOM.div(null, "Strength: ", questor.currentAttributes.strength),
        React.DOM.div(null, "Health: ", questor.currentAttributes.health),
        React.DOM.div(null, "Speed: ", questor.currentAttributes.speed),
        React.DOM.div(null, "xPS: ", questor.currentAttributes.perSecond),
        React.DOM.div(null, "First Skill: ", questor.currentAttributes.skills.a),
        React.DOM.div(null, "Second Skill: ", questor.currentAttributes.skills.b)
      )
    );
  },
  rarityToStars: require("../util/rarityToStars")
});
