"use strict";
var React = require("react");
var Router = require("react-router-component");
var Link = Router.Link;

var capitalize = function (s) {
  return s.charAt(0).toUpperCase() + s.slice(1);
};

module.exports = React.createClass({
  displayName: "QuestorDetail",
  render: function () {
    return (
      React.DOM.div({ className: "qube-questor-detail"},
        React.DOM.div(null, this.props.name),
        React.DOM.div(null, this.rarityToStars(this.props.rarity)),
        React.DOM.div(null, "Affinity: ", Link({ href: this.props.affinity + "/0/all" }, capitalize(this.props.affinity))),
        React.DOM.div(null, "Origin: ", Link({ href: "/all/0/" + this.props.origin }, this.props.origin)),
        React.DOM.div(null, "Description: ", this.props.description),
        React.DOM.div(null, "Strength: ", this.props.baseAttributes.strengthLow),
        React.DOM.div(null, "Health: ", this.props.baseAttributes.healthLow),
        React.DOM.div(null, "Speed: ", this.props.baseAttributes.speedLow),
        React.DOM.div(null, "xPS: ", this.props.baseAttributes.perSecond),
        React.DOM.div(null, "First Skill: ", this.props.baseAttributes.skills.a),
        React.DOM.div(null, "Second Skill: ", this.props.baseAttributes.skills.b)
      )
    );
  },
  rarityToStars: require("../util/rarityToStars")
});
