"use strict";
var React = require("react");
var Router = require("react-router-component");
var Link = Router.Link;

var rarityToStars = require("qubeulator-components/util/rarityToStars");

module.exports = React.createClass({
  displayName: "QuestorList",
  render: function () {
    var rows = [];
    this.props.questors.forEach(function (questor) {
      rows.push(
        React.DOM.li(null,
          Link({ href: "/detail/" + questor.name }, questor.name),
          React.DOM.span(null, rarityToStars(questor.rarity))
        )
      );
    });

    return (
      React.DOM.ul({ className: "qube-questor-list" }, rows)
    );
  }
});
